import React, { useMemo } from 'react';
import './ProductSummary.css';

const ProductSummary = ({ combinedData }) => {
  const resumoProdutos = useMemo(() => {
    const resumo = {};

    combinedData.forEach((item) => {
      const codigoProduto = item.Codigo;
      const quantidadePrevista = item['Quantidade Prevista'] || 0;
      const quantidadeEntregue = item.QuantidadeEntregue || 0;

      if (!resumo[codigoProduto]) {
        resumo[codigoProduto] = {
          Codigo: codigoProduto,
          QuantidadePrevista: 0,
          QuantidadeEntregue: 0,
        };
      }

      resumo[codigoProduto].QuantidadePrevista += quantidadePrevista;
      resumo[codigoProduto].QuantidadeEntregue += quantidadeEntregue;
    });

    return Object.values(resumo);
  }, [combinedData]);

  return (
    <div>
      <h2>Resumo por Produto</h2>
      <table className="data-table">
        <thead>
          <tr>
            <th>CÃ³digo do Produto</th>
            <th>Quantidade Prevista</th>
            <th>Quantidade Entregue</th>
          </tr>
        </thead>
        <tbody>
          {resumoProdutos.map((produto) => (
            <tr key={produto.Codigo}>
              <td>{produto.Codigo}</td>
              <td>{produto.QuantidadePrevista}</td>
              <td>{produto.QuantidadeEntregue}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductSummary;
