import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Papa from 'papaparse';
import './App.css';

const App = () => {
  const [previsaoData, setPrevisaoData] = useState([]);
  const [entregaData, setEntregaData] = useState([]);
  const [combinedData, setCombinedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filterMonth, setFilterMonth] = useState('');
  const [filterYear, setFilterYear] = useState('');

  // Função para buscar os dados da aba Previsao
  const fetchPrevisaoData = () => {
    const previsaoUrl = 'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/gviz/tq?tqx=out:csv&sheet=Previsao';

    axios.get(previsaoUrl)
      .then(response => {
        Papa.parse(response.data, {
          complete: (result) => {
            setPrevisaoData(result.data);
          },
          header: true
        });
      })
      .catch(error => {
        console.error("Erro ao buscar dados da aba Previsão:", error);
        setError("Erro ao carregar os dados da aba Previsão.");
      });
  };

  // Função para buscar os dados da aba Entrega
  const fetchEntregaData = () => {
    const entregaUrl = 'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/gviz/tq?tqx=out:csv&sheet=Entrega';

    axios.get(entregaUrl)
      .then(response => {
        Papa.parse(response.data, {
          complete: (result) => {
            setEntregaData(result.data);
          },
          header: true
        });
      })
      .catch(error => {
        console.error("Erro ao buscar dados da aba Entrega:", error);
        setError("Erro ao carregar os dados da aba Entrega.");
      });
  };

  // Combinar os dados com base no código do produto e calcular a quantidade restante
  useEffect(() => {
    if (previsaoData.length > 0 && entregaData.length > 0) {
      const combined = previsaoData.map(previsaoItem => {
        const entregaItem = entregaData.find(item => item['Codigo'] === previsaoItem['Codigo']);
        const quantidadeEntregue = entregaItem ? entregaItem['Quantidade Entregue'] : 0;
        const quantidadePrevista = previsaoItem['Quantidade Prevista'] || 0;
        
        // Calculando a quantidade restante
        const quantidadeRestante = quantidadePrevista - quantidadeEntregue;

        return {
          ...previsaoItem,
          QuantidadeEntregue: quantidadeEntregue,
          DataEntregaReal: entregaItem ? entregaItem['Data de Entrega (Real)'] : 'N/A',
          QuantidadeRestante: quantidadeRestante // Não ajustamos mais para zero
        };
      });
      setCombinedData(combined);
      setLoading(false);
    }
  }, [previsaoData, entregaData]);

  // Carregar dados inicialmente
  useEffect(() => {
    fetchPrevisaoData();
    fetchEntregaData();
  }, []);

  // Função para aplicar filtro de mês e ano baseado na data de previsão
  const filterData = () => {
    return combinedData.filter(item => {
      const dataPrevisao = new Date(item['Data de Previsão de Entrega']);
      const mes = dataPrevisao.getMonth() + 1; // getMonth retorna 0 (janeiro) a 11 (dezembro)
      const ano = dataPrevisao.getFullYear();

      const mesFiltro = filterMonth ? mes === parseInt(filterMonth) : true;
      const anoFiltro = filterYear ? ano === parseInt(filterYear) : true;

      return mesFiltro && anoFiltro;
    });
  };

  return (
    <div>
      <h1>Dados Compilados</h1>
      {loading && <p>Carregando...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {!loading && !error && (
        <div>
          {/* Filtros de Mês e Ano */}
          <div>
            <label>Escolha o Mês: </label>
            <select onChange={(e) => setFilterMonth(e.target.value)} value={filterMonth}>
              <option value="">Todos</option>
              {[...Array(12).keys()].map(i => (
                <option key={i} value={i + 1}>
                  {new Date(0, i).toLocaleString('pt-BR', { month: 'long' })}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label>Escolha o Ano: </label>
            <select onChange={(e) => setFilterYear(e.target.value)} value={filterYear}>
              <option value="">Todos</option>
              {[...Array(5).keys()].map(i => {
                const year = new Date().getFullYear() - i;
                return (
                  <option key={year} value={year}>
                    {year}
                  </option>
                );
              })}
            </select>
          </div>

          {/* Tabela de dados filtrados */}
          <table>
            <thead>
              <tr>
                <th>Código</th> {/* Alterei o nome da coluna para "Código" */}
                <th>Produto</th>
                <th>Quantidade Prevista</th>
                <th>Data de Previsão de Entrega</th>
                <th>Semana</th>
                <th>Quantidade Entregue</th>
                <th>Data de Entrega Real</th>
                <th>Quantidade Restante</th>
              </tr>
            </thead>
            <tbody>
              {filterData().map((row, index) => (
                <tr key={index}>
                  <td>{row['Codigo']}</td> {/* Alterei para usar 'Codigo' */}
                  <td>{row['Produto']}</td>
                  <td>{row['Quantidade Prevista']}</td>
                  <td>{row['Data de Previsão de Entrega']}</td>
                  <td>{row['Semana']}</td>
                  <td>{row['QuantidadeEntregue']}</td>
                  <td>{row['DataEntregaReal']}</td>
                  <td>{row['QuantidadeRestante']}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
