import React, { useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import Papa from 'papaparse';
import './App.css';

const axiosSource = axios.CancelToken.source();

const App = () => {
  const [previsaoData, setPrevisaoData] = useState([]);
  const [entregaData, setEntregaData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [periodoFiltro, setPeriodoFiltro] = useState('30_dias'); // Filtro de período
  const [dataInicio, setDataInicio] = useState(''); // Data inicial para intervalo customizado
  const [dataFim, setDataFim] = useState(''); // Data final para intervalo customizado
  const [produtoFiltro, setProdutoFiltro] = useState(''); // Filtro por nome do produto

  // Função para converter datas no formato dd/mm/aaaa para um objeto Date
  const parseDate = (dateString) => {
    if (!dateString) return null;

    const [day, month, year] = dateString.split('/');
    return new Date(`${year}-${month}-${day}`);
  };

  // Função genérica para buscar dados de uma planilha
  const fetchSheetData = async (url, setData) => {
    try {
      const response = await axios.get(url, {
        cancelToken: axiosSource.token,
      });

      return new Promise((resolve, reject) => {
        Papa.parse(response.data, {
          header: true,
          skipEmptyLines: true,
          dynamicTyping: true,
          complete: (result) => {
            setData(result.data);
            resolve();
          },
          error: (error) => reject(error),
        });
      });
    } catch (error) {
      if (!axios.isCancel(error)) {
        throw error;
      }
    }
  };

  // Função para calcular a semana do mês
  const getWeekOfMonth = (dateString) => {
    const date = parseDate(dateString);
    if (!date || isNaN(date)) return 'Data inválida';

    const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
    const dayOfMonth = date.getDate();
    const adjustedDate = dayOfMonth + firstDay.getDay() - 1;
    return Math.ceil(adjustedDate / 7);
  };

  // Combinar os dados das abas Previsão e Entrega
  const combinedData = useMemo(() => {
    if (!previsaoData.length || !entregaData.length) return [];

    return previsaoData.map((previsaoItem) => {
      const entregas = entregaData.filter(
        (item) =>
          item.Codigo === previsaoItem.Codigo &&
          item['Data Referente (Planejada)'] === previsaoItem['Data de Previsão de Entrega']
      );

      const quantidadeEntregue = entregas.reduce(
        (acc, curr) => acc + (curr['Quantidade Entregue'] || 0),
        0
      );

      const quantidadeFaltante = Math.max(
        (previsaoItem['Quantidade Prevista'] || 0) - quantidadeEntregue,
        0
      );

      return {
        ...previsaoItem,
        QuantidadeEntregue: quantidadeEntregue,
        QuantidadeFaltante: quantidadeFaltante,
        SemanaPrevisao: `${getWeekOfMonth(previsaoItem['Data de Previsão de Entrega'])}ª Semana`,
      };
    });
  }, [previsaoData, entregaData]);

  // Lista de produtos únicos para o filtro
  const produtosUnicos = useMemo(() => {
    const produtos = combinedData.map((item) => item.Produto);
    return [...new Set(produtos)]; // Remove duplicatas
  }, [combinedData]);

  // Função para aplicar os filtros
  const dadosFiltrados = useMemo(() => {
    if (!combinedData.length) return [];

    let filtrados = combinedData;

    // Filtro por período
    const hoje = new Date();
    switch (periodoFiltro) {
      case '30_dias':
        const trintaDiasAtras = new Date();
        trintaDiasAtras.setDate(hoje.getDate() - 30);
        filtrados = filtrados.filter((item) => {
          const dataPrevisao = parseDate(item['Data de Previsão de Entrega']);
          return dataPrevisao >= trintaDiasAtras && dataPrevisao <= hoje;
        });
        break;

      case 'mes_passado':
        const primeiroDiaMesPassado = new Date(hoje.getFullYear(), hoje.getMonth() - 1, 1);
        const ultimoDiaMesPassado = new Date(hoje.getFullYear(), hoje.getMonth(), 0);
        filtrados = filtrados.filter((item) => {
          const dataPrevisao = parseDate(item['Data de Previsão de Entrega']);
          return dataPrevisao >= primeiroDiaMesPassado && dataPrevisao <= ultimoDiaMesPassado;
        });
        break;

      case 'mes_atual':
        const primeiroDiaMesAtual = new Date(hoje.getFullYear(), hoje.getMonth(), 1);
        const ultimoDiaMesAtual = new Date(hoje.getFullYear(), hoje.getMonth() + 1, 0);
        filtrados = filtrados.filter((item) => {
          const dataPrevisao = parseDate(item['Data de Previsão de Entrega']);
          return dataPrevisao >= primeiroDiaMesAtual && dataPrevisao <= ultimoDiaMesAtual;
        });
        break;

      case 'customizado':
        if (dataInicio && dataFim) {
          const inicio = new Date(dataInicio);
          const fim = new Date(dataFim);
          filtrados = filtrados.filter((item) => {
            const dataPrevisao = parseDate(item['Data de Previsão de Entrega']);
            return dataPrevisao >= inicio && dataPrevisao <= fim;
          });
        }
        break;

      default:
        break;
    }

    // Filtro por nome do produto
    if (produtoFiltro) {
      filtrados = filtrados.filter((item) => item.Produto === produtoFiltro);
    }

    return filtrados;
  }, [combinedData, periodoFiltro, dataInicio, dataFim, produtoFiltro]);

  // Carregar dados ao montar o componente
  useEffect(() => {
    const fetchData = async () => {
      try {
        setError(null);
        setLoading(true);

        await Promise.all([
          fetchSheetData(
            'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/gviz/tq?tqx=out:csv&sheet=Previsao',
            setPrevisaoData
          ),
          fetchSheetData(
            'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/gviz/tq?tqx=out:csv&sheet=Entrega',
            setEntregaData
          ),
        ]);
      } catch (error) {
        console.error('Erro ao carregar dados:', error);
        setError('Erro ao carregar dados. Tente recarregar a página.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    return () => axiosSource.cancel('Componente desmontado');
  }, []);

  // Função para estilizar a célula de quantidade faltante
  const getFaltanteStyle = (value) => {
    if (value <= 0) return 'faltante-green';
    if (value <= 100) return 'faltante-yellow';
    return 'faltante-red';
  };

  return (
    <div className="container">
      <h1>Dados Compilados</h1>

      {/* Filtros */}
      <div className="filtros">
        <label>
          Período:
          <select
            value={periodoFiltro}
            onChange={(e) => setPeriodoFiltro(e.target.value)}
          >
            <option value="30_dias">Últimos 30 dias</option>
            <option value="mes_passado">Mês Passado</option>
            <option value="mes_atual">Mês Atual</option>
            <option value="customizado">Intervalo Customizado</option>
          </select>
        </label>

        {periodoFiltro === 'customizado' && (
          <div>
            <label>
              Data Início:
              <input
                type="date"
                value={dataInicio}
                onChange={(e) => setDataInicio(e.target.value)}
              />
            </label>
            <label>
              Data Fim:
              <input
                type="date"
                value={dataFim}
                onChange={(e) => setDataFim(e.target.value)}
              />
            </label>
          </div>
        )}

        <label>
          Produto:
          <select
            value={produtoFiltro}
            onChange={(e) => setProdutoFiltro(e.target.value)}
          >
            <option value="">Todos</option>
            {produtosUnicos.map((produto) => (
              <option key={produto} value={produto}>
                {produto}
              </option>
            ))}
          </select>
        </label>
      </div>

      {loading && <div className="loading">Carregando...</div>}

      {error && <div className="error">{error}</div>}

      {!loading && !error && (
        <table className="data-table">
          <thead>
            <tr>
              {[
                'Código',
                'Produto',
                'Quantidade Prevista',
                'Data de Previsão',
                'Semana',
                'Entregue',
                'Faltante',
              ].map((header) => (
                <th key={header}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {dadosFiltrados.map((row, index) => {
              const dataPrevisao = parseDate(row['Data de Previsão de Entrega']);

              return (
                <tr key={`${row.Codigo}-${row['Data de Previsão de Entrega']}-${index}`}>
                  <td>{row.Codigo}</td>
                  <td>{row.Produto}</td>
                  <td>{row['Quantidade Prevista']}</td>
                  <td>{dataPrevisao ? dataPrevisao.toLocaleDateString('pt-BR') : 'N/A'}</td>
                  <td>{row.SemanaPrevisao}</td>
                  <td>{row.QuantidadeEntregue}</td>
                  <td className={getFaltanteStyle(row.QuantidadeFiltante)}>
                    {row.QuantidadeFaltante}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default App;
