import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Papa from 'papaparse';
import './App.css';  // Importando o arquivo CSS

const App = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Função para buscar os dados
  const fetchData = () => {
    const sheetUrl = 'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/export?format=csv';

    setLoading(true);
    setError(null);

    axios.get(sheetUrl)
      .then(response => {
        Papa.parse(response.data, {
          complete: (result) => {
            setData(result.data);
            setLoading(false);
          },
          header: true // Usar as primeiras linhas como cabeçalho
        });
      })
      .catch(error => {
        console.error("Erro ao buscar dados da planilha:", error);
        setError("Erro ao carregar os dados. Tente novamente mais tarde.");
        setLoading(false);
      });
  };

  // Carregar dados inicialmente
  useEffect(() => {
    fetchData();
    
    // Atualiza os dados a cada 1 minuto (60000 ms)
    const interval = setInterval(fetchData, 60000);

    // Limpeza do intervalo quando o componente for desmontado
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h1>Dados da Planilha</h1>
      {loading && <p>Carregando...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {!loading && !error && (
        <table>
          <thead>
            <tr>
              <th>Produto</th>
              <th>Quantidade Prevista</th>
              <th>Data de Previsão de Entrega</th>
              <th>Semana</th>
            </tr>
          </thead>
          <tbody>
            {data.map((row, index) => (
              <tr key={index}>
                <td>{row['Produto']}</td>
                <td>{row['Quantidade Prevista']}</td>
                <td>{row['Data de Previsão de Entrega']}</td>
                <td>{row['Semana']}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
