// ProductSummary.js
import React, { useMemo } from 'react';

const ProductSummary = ({ combinedData }) => {
  // Função para calcular as somas
  const totalSums = useMemo(() => {
    return combinedData.reduce(
      (acc, { totalPrevisto, totalEntregue }) => {
        acc.totalPrevisto += totalPrevisto;
        acc.totalEntregue += totalEntregue;
        return acc;
      },
      { totalPrevisto: 0, totalEntregue: 0 }
    );
  }, [combinedData]);

  return (
    <div className="product-summary">
      <h2>Resumo dos Produtos</h2>
      <table className="data-table">
        <thead>
          <tr>
            <th>Produto</th>
            <th>Total Previsto</th>
            <th>Total Entregue</th>
          </tr>
        </thead>
        <tbody>
          {combinedData.map(({ produto, totalPrevisto, totalEntregue }) => (
            <tr key={produto}>
              <td>{produto}</td>
              <td>{totalPrevisto}</td>
              <td>{totalEntregue}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="summary-total">
        <h3>Total Geral</h3>
        <p>Total Previsto: {totalSums.totalPrevisto}</p>
        <p>Total Entregue: {totalSums.totalEntregue}</p>
      </div>
    </div>
  );
};

export default ProductSummary;
