import React, { useMemo } from 'react';
import './ProductSummary.css';

const ProductSummary = ({ combinedData }) => {
  const productSummary = useMemo(() => {
    const summary = {};

    combinedData.forEach((item) => {
      const codigo = item.Codigo;

      if (!summary[codigo]) {
        summary[codigo] = {
          Codigo: codigo,
          QuantidadePrevista: 0,
          QuantidadeEntregue: 0,
        };
      }

      summary[codigo].QuantidadePrevista += item['Quantidade Prevista'] || 0;
      summary[codigo].QuantidadeEntregue += item.QuantidadeEntregue || 0;
    });

    return Object.values(summary);
  }, [combinedData]);

  return (
    <div className="summary-container">
      <h2>Resumo por Produto</h2>
      <table className="summary-table">
        <thead>
          <tr>
            <th>CÃ³digo</th>
            <th>Quantidade Prevista</th>
            <th>Quantidade Entregue</th>
          </tr>
        </thead>
        <tbody>
          {productSummary.map((item) => (
            <tr key={item.Codigo}>
              <td>{item.Codigo}</td>
              <td>{item.QuantidadePrevista}</td>
              <td>{item.QuantidadeEntregue}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductSummary;
