import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Papa from 'papaparse';
import './App.css';

const App = () => {
  const [previsaoData, setPrevisaoData] = useState([]);
  const [entregaData, setEntregaData] = useState([]);
  const [combinedData, setCombinedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [filterPeriod, setFilterPeriod] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [productFilter, setProductFilter] = useState(''); // Estado para armazenar o filtro de nome do produto

  // Função para converter a data no formato DD/MM/YYYY para o formato YYYY-MM-DD
  const convertDate = (dateString) => {
    const [day, month, year] = dateString.split('/');
    return new Date(`${year}-${month}-${day}`);
  };

  // Função para calcular a semana do mês
  const getWeekOfMonth = (date) => {
    if (isNaN(date)) {
      return 0;
    }
    const startOfMonth = new Date(date.getFullYear(), date.getMonth(), 1);
    const dayOfMonth = date.getDate();
    return Math.ceil(dayOfMonth / 7);
  };

  // Função para retornar o nome da semana do mês
  const getWeekName = (weekNumber) => {
    const weeks = [
      '1ª Semana', '2ª Semana', '3ª Semana', '4ª Semana'
    ];
    return weeks[weekNumber - 1] || 'Semana Indefinida';
  };

  // Função para buscar os dados da aba Previsao
  const fetchPrevisaoData = () => {
    const previsaoUrl = 'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/gviz/tq?tqx=out:csv&sheet=Previsao';

    axios.get(previsaoUrl)
      .then(response => {
        Papa.parse(response.data, {
          complete: (result) => {
            setPrevisaoData(result.data);
          },
          header: true
        });
      })
      .catch(error => {
        console.error("Erro ao buscar dados da aba Previsão:", error);
        setError("Erro ao carregar os dados da aba Previsão.");
      });
  };

  // Função para buscar os dados da aba Entrega
  const fetchEntregaData = () => {
    const entregaUrl = 'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/gviz/tq?tqx=out:csv&sheet=Entrega';

    axios.get(entregaUrl)
      .then(response => {
        Papa.parse(response.data, {
          complete: (result) => {
            setEntregaData(result.data);
          },
          header: true
        });
      })
      .catch(error => {
        console.error("Erro ao buscar dados da aba Entrega:", error);
        setError("Erro ao carregar os dados da aba Entrega.");
      });
  };

  // Combinar os dados com base no Código do Produto e calcular a quantidade faltante e semana da previsão
  useEffect(() => {
    if (previsaoData.length > 0 && entregaData.length > 0) {
      const combined = previsaoData.map(previsaoItem => {
        const entregaItem = entregaData.find(item => item['Codigo'] === previsaoItem['Codigo']);
        const quantidadePrevista = parseFloat(previsaoItem['Quantidade Prevista']) || 0;
        const quantidadeEntregue = parseFloat(entregaItem ? entregaItem['Quantidade Entregue'] : 0) || 0;
        const quantidadeFaltante = quantidadePrevista - quantidadeEntregue;

        const previsaoDate = convertDate(previsaoItem['Data de Previsão de Entrega']);
        const semanaPrevisao = isNaN(previsaoDate) ? 'Semana Indefinida' : getWeekName(getWeekOfMonth(previsaoDate));

        return {
          ...previsaoItem,
          QuantidadeEntregue: entregaItem ? entregaItem['Quantidade Entregue'] : 'N/A',
          DataEntregaReal: entregaItem ? entregaItem['Data de Entrega (Real)'] : 'N/A',
          DataPrevisao: previsaoItem['Data de Previsão de Entrega'],
          QuantidadeFaltante: quantidadeFaltante,
          SemanaPrevisao: semanaPrevisao
        };
      });
      setCombinedData(combined);
      setLoading(false);
    }
  }, [previsaoData, entregaData]);

  // Carregar dados inicialmente
  useEffect(() => {
    fetchPrevisaoData();
    fetchEntregaData();
  }, []);

  // Função para aplicar a cor com base na quantidade faltante
  const getColorForQuantity = (quantidadeFaltante) => {
    if (quantidadeFaltante > 0) {
      const red = Math.min(255, quantidadeFaltante * 20); // Quanto maior, mais vermelho
      return `rgb(${red}, 0, 0)`; // Cor do vermelho
    } else if (quantidadeFaltante === 0) {
      return 'rgb(0, 128, 0)'; // Verde
    } else {
      const yellow = Math.max(0, Math.min(255, -quantidadeFaltante * 20)); // Quanto mais próximo de zero, mais amarelo
      return `rgb(${yellow}, ${yellow}, 0)`; // Cor amarela
    }
  };

  // Filtro por período
  const handlePeriodChange = (e) => {
    setFilterPeriod(e.target.value);
    setLoading(true);
    let filteredData = [];

    const today = new Date();

    if (e.target.value === 'last30') {
      const last30Days = new Date();
      last30Days.setDate(today.getDate() - 30);
      filteredData = combinedData.filter(item => {
        const date = convertDate(item.DataPrevisao);
        return date >= last30Days && date <= today;
      });
    } else if (e.target.value === 'lastMonth') {
      const firstDayOfLastMonth = new Date(today.getFullYear(), today.getMonth() - 1, 1);
      const lastDayOfLastMonth = new Date(today.getFullYear(), today.getMonth(), 0);
      filteredData = combinedData.filter(item => {
        const date = convertDate(item.DataPrevisao);
        return date >= firstDayOfLastMonth && date <= lastDayOfLastMonth;
      });
    } else if (e.target.value === 'thisMonth') {
      const firstDayOfThisMonth = new Date(today.getFullYear(), today.getMonth(), 1);
      const lastDayOfThisMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0);
      filteredData = combinedData.filter(item => {
        const date = convertDate(item.DataPrevisao);
        return date >= firstDayOfThisMonth && date <= lastDayOfThisMonth;
      });
    } else if (e.target.value === 'custom') {
      if (startDate && endDate) {
        const customStartDate = new Date(startDate);
        const customEndDate = new Date(endDate);
        filteredData = combinedData.filter(item => {
          const date = convertDate(item.DataPrevisao);
          return date >= customStartDate && date <= customEndDate;
        });
      }
    }
    setCombinedData(filteredData);
    setLoading(false);
  };

  // Custom Date Range
  const handleStartDateChange = (e) => {
    setStartDate(e.target.value);
  };

  const handleEndDateChange = (e) => {
    setEndDate(e.target.value);
  };

  // Filtro de nome do produto
  const handleProductFilterChange = (e) => {
    setProductFilter(e.target.value);
  };

  // Filtrar dados por nome do produto
  const filteredData = combinedData.filter(item =>
    item.Produto.toLowerCase().includes(productFilter.toLowerCase())
  );

  return (
    <div className="app-container">
      <h1 className="title">Dados de Previsão e Entrega</h1>

      <div className="filter-container">
        <label>
          Selecione um Período:
          <select value={filterPeriod} onChange={handlePeriodChange}>
            <option value="">Selecione</option>
            <option value="last30">Últimos 30 dias</option>
            <option value="lastMonth">Mês Passado</option>
            <option value="thisMonth">Mês Atual</option>
            <option value="custom">Período Personalizado</option>
          </select>
        </label>

        {filterPeriod === 'custom' && (
          <>
            <input type="date" value={startDate} onChange={handleStartDateChange} />
            <input type="date" value={endDate} onChange={handleEndDateChange} />
          </>
        )}

        <label>
          Filtrar por Produto:
          <input
            type="text"
            placeholder="Nome do Produto"
            value={productFilter}
            onChange={handleProductFilterChange}
          />
        </label>
      </div>

      {loading ? (
        <p>Carregando...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Código</th>
                <th>Produto</th>
                <th>Quantidade Prevista</th>
                <th>Data de Previsão de Entrega</th>
                <th>Semana da Previsão</th>
                <th>Quantidade Entregue</th>
                <th>Data de Entrega Real</th>
                <th>Quantidade Faltante</th>
              </tr>
            </thead>
            <tbody>
              {filteredData.map((item, index) => (
                <tr key={index}>
                  <td>{item.Codigo}</td>
                  <td>{item.Produto}</td>
                  <td>{item['Quantidade Prevista']}</td>
                  <td>{item.DataPrevisao}</td>
                  <td>{item.SemanaPrevisao}</td>
                  <td>{item.QuantidadeEntregue}</td>
                  <td>{item.DataEntregaReal}</td>
                  <td style={{ color: getColorForQuantity(item.QuantidadeFaltante) }}>
                    {item.QuantidadeFaltante}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default App;
