import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [data, setData] = useState([]);  // Para armazenar os dados da planilha
  const [loading, setLoading] = useState(true);  // Para mostrar o carregamento

  // Função para buscar os dados da planilha
  const fetchData = async () => {
    try {
      // Substitua pela URL da sua API
      const response = await axios.get('https://sheets.googleapis.com/v4/spreadsheets/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/values/Sheet1?key=AIzaSyDjQkkzbl0VCCBFMc7sDWliSu6sHAykP3A');
      
      // A planilha pode ter dados na primeira coluna que você quer exibir
      const rows = response.data.values;
      if (rows) {
        setData(rows);  // Atualiza o estado com os dados
      }
    } catch (error) {
      console.error("Erro ao buscar os dados da planilha:", error);
    } finally {
      setLoading(false);  // Quando terminar de carregar, não mostre mais a mensagem de carregamento
    }
  };

  // Carregar os dados ao iniciar o componente
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="App">
      <h1>Dados da Planilha</h1>

      {loading ? (
        <p>Carregando dados...</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Coluna 1</th>
              <th>Coluna 2</th>
              <th>Coluna 3</th>
              {/* Adicione mais colunas conforme necessário */}
            </tr>
          </thead>
          <tbody>
            {data.map((row, index) => (
              <tr key={index}>
                <td>{row[0]}</td> {/* Exemplo: exibe o primeiro valor da linha */}
                <td>{row[1]}</td> {/* Exibe o segundo valor da linha */}
                <td>{row[2]}</td> {/* Exibe o terceiro valor da linha */}
                {/* Adapte conforme o número de colunas que sua planilha tem */}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
