import React, { useEffect, useState } from 'react';
import axios from 'axios';

const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // O link para acessar os dados no formato JSON
    const sheetUrl = 'https://spreadsheets.google.com/feeds/cells/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/1/public/full?alt=json';

    // Fazer requisição com Axios para pegar os dados da planilha
    axios.get(sheetUrl)
      .then(response => {
        const entries = response.data.feed.entry;
        const rows = [];

        // Organize os dados conforme o formato da planilha
        for (let i = 0; i < entries.length; i += 4) {
          rows.push([
            entries[i].content.$t,       // Produto
            entries[i + 1].content.$t,   // Quantidade Prevista
            entries[i + 2].content.$t,   // Data de Previsão
            entries[i + 3].content.$t    // Semana
          ]);
        }
        setData(rows);
      })
      .catch(error => {
        console.error("Erro ao buscar dados da planilha:", error);
      });
  }, []);

  return (
    <div>
      <h1>Dados da Planilha</h1>
      <table>
        <thead>
          <tr>
            <th>Produto</th>
            <th>Quantidade Prevista</th>
            <th>Data de Previsão de Entrega</th>
            <th>Semana</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row[0]}</td>
              <td>{row[1]}</td>
              <td>{row[2]}</td>
              <td>{row[3]}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
