// App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import axios from 'axios';
import Papa from 'papaparse';
import Resumo from './Resumo'; // Importa a página de resumo
import './App.css';

const axiosSource = axios.CancelToken.source();

const App = () => {
  const [previsaoData, setPrevisaoData] = useState([]);
  const [entregaData, setEntregaData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Carregar dados ao montar o componente
  useEffect(() => {
    const fetchData = async () => {
      try {
        setError(null);
        setLoading(true);

        await Promise.all([
          fetchSheetData(
            'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/gviz/tq?tqx=out:csv&sheet=Previsao',
            setPrevisaoData
          ),
          fetchSheetData(
            'https://docs.google.com/spreadsheets/d/1dgVHy_i0djzz8ncd1L2zOkzI3qy7YSbT7CvV_BPgXm0/gviz/tq?tqx=out:csv&sheet=Entrega',
            setEntregaData
          ),
        ]);
      } catch (error) {
        console.error('Erro ao carregar dados:', error);
        setError('Erro ao carregar dados. Verifique os links ou tente novamente mais tarde.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    return () => axiosSource.cancel('Componente desmontado');
  }, []);

  // Função genérica para buscar dados de uma planilha
  const fetchSheetData = async (url, setData) => {
    try {
      const response = await axios.get(url, {
        cancelToken: axiosSource.token,
      });

      return new Promise((resolve, reject) => {
        Papa.parse(response.data, {
          header: true,
          skipEmptyLines: true,
          dynamicTyping: true,
          complete: (result) => {
            if (result.data && result.data.length > 0) {
              setData(result.data);
              resolve();
            } else {
              reject(new Error('Dados não encontrados na planilha.'));
            }
          },
          error: (error) => reject(error),
        });
      });
    } catch (error) {
      if (!axios.isCancel(error)) {
        throw error;
      }
    }
  };

  return (
    <Router>
      <div className="container">
        <h1>Dados Compilados</h1>

        <nav>
          <Link to={{ pathname: '/resumo', state: { previsaoData, entregaData } }}>
            Ver Resumo
          </Link>
        </nav>

        <Switch>
          <Route path="/resumo" component={Resumo} />
        </Switch>
      </div>
    </Router>
  );
};

export default App;
