import React, { useState } from 'react';
import './ProductSummary.css';

const ProductSummary = ({ combinedData }) => {
  const [produtoFiltro, setProdutoFiltro] = useState('');
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');

  const handleFiltroProduto = (e) => setProdutoFiltro(e.target.value);

  const handleDataInicio = (e) => setDataInicio(e.target.value);
  const handleDataFim = (e) => setDataFim(e.target.value);

  const dadosFiltrados = combinedData.filter((item) => {
    const dataItem = new Date(item['Data de Previsão de Entrega']);

    const filtroProduto = produtoFiltro
      ? item.Codigo?.toLowerCase().includes(produtoFiltro.toLowerCase())
      : true;

    const filtroData =
      (!dataInicio || dataItem >= new Date(dataInicio)) &&
      (!dataFim || dataItem <= new Date(dataFim));

    return filtroProduto && filtroData;
  });

  return (
    <div>
      <h2>Resumo por Produto</h2>

      <div className="filtros">
        <input
          type="text"
          placeholder="Filtrar por código do produto"
          value={produtoFiltro}
          onChange={handleFiltroProduto}
        />
        <input type="date" value={dataInicio} onChange={handleDataInicio} />
        <input type="date" value={dataFim} onChange={handleDataFim} />
      </div>

      <table>
        <thead>
          <tr>
            <th>Código</th>
            <th>Produto</th>
            <th>Data de Previsão</th>
            <th>Quantidade Prevista</th>
            <th>Quantidade Entregue</th>
            <th>Quantidade Faltante</th>
          </tr>
        </thead>
        <tbody>
          {dadosFiltrados.map((item, index) => (
            <tr key={index}>
              <td>{item.Codigo}</td>
              <td>{item.Produto}</td>
              <td>{item['Data de Previsão de Entrega']}</td>
              <td>{item['Quantidade Prevista']}</td>
              <td>{item.QuantidadeEntregue}</td>
              <td>{item.QuantidadeFaltante}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductSummary;
