import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Papa from 'papaparse';
import './App.css';

const App = () => {
  const [previsaoData, setPrevisaoData] = useState([]);
  const [entregaData, setEntregaData] = useState([]);
  const [combinedData, setCombinedData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filtroProduto, setFiltroProduto] = useState('');
  const [filtroMes, setFiltroMes] = useState('');
  const [filtroAno, setFiltroAno] = useState('');

  // Função para buscar os dados da aba Previsao
  const fetchPrevisaoData = () => {
    const previsaoUrl = 'https://docs.google.com/spreadsheets/d/{ID_da_planilha}/gviz/tq?tqx=out:csv&sheet=Previsao';

    axios.get(previsaoUrl)
      .then(response => {
        Papa.parse(response.data, {
          complete: (result) => {
            setPrevisaoData(result.data);
          },
          header: true
        });
      })
      .catch(error => {
        console.error("Erro ao buscar dados da aba Previsão:", error);
        setError("Erro ao carregar os dados da aba Previsão.");
      });
  };

  // Função para buscar os dados da aba Entrega
  const fetchEntregaData = () => {
    const entregaUrl = 'https://docs.google.com/spreadsheets/d/{ID_da_planilha}/gviz/tq?tqx=out:csv&sheet=Entrega';

    axios.get(entregaUrl)
      .then(response => {
        Papa.parse(response.data, {
          complete: (result) => {
            setEntregaData(result.data);
          },
          header: true
        });
      })
      .catch(error => {
        console.error("Erro ao buscar dados da aba Entrega:", error);
        setError("Erro ao carregar os dados da aba Entrega.");
      });
  };

  // Combinar os dados com base no código do produto
  useEffect(() => {
    if (previsaoData.length > 0 && entregaData.length > 0) {
      const combined = previsaoData.map(previsaoItem => {
        // Encontrar o item correspondente na aba Entrega com base no código do produto e na data de previsão
        const entregaItem = entregaData.find(item => item['Codigo'] === previsaoItem['Codigo'] && item['Data Referente (Planejada)'] === previsaoItem['Data de Previsão de Entrega']);

        // Calcular a semana com base na Data de Previsão de Entrega
        const dataPrevisao = new Date(previsaoItem['Data de Previsão de Entrega']);
        const semanaPrevisao = Math.ceil((dataPrevisao.getDate() - 1) / 7) + 'ª Semana';

        // Preencher os dados combinados
        return {
          ...previsaoItem,
          QuantidadeEntregue: entregaItem ? entregaItem['Quantidade Entregue'] : 'N/A',
          DataEntregaReal: entregaItem ? entregaItem['Data de Entrega Real'] : 'N/A',
          SemanaPrevisao: semanaPrevisao,
          // Calcular a quantidade faltante
          QuantidadeFaltante: entregaItem ? previsaoItem['Quantidade Prevista'] - entregaItem['Quantidade Entregue'] : previsaoItem['Quantidade Prevista']
        };
      });

      // Filtrar pelos critérios de filtro se existirem
      const filteredData = combined.filter(item => {
        const dataPrevisao = new Date(item['Data de Previsão de Entrega']);
        const mes = dataPrevisao.getMonth() + 1;
        const ano = dataPrevisao.getFullYear();

        const filterByProduct = filtroProduto ? item['Produto'].toLowerCase().includes(filtroProduto.toLowerCase()) : true;
        const filterByMonth = filtroMes ? mes === parseInt(filtroMes) : true;
        const filterByYear = filtroAno ? ano === parseInt(filtroAno) : true;

        return filterByProduct && filterByMonth && filterByYear;
      });

      setCombinedData(filteredData);
      setLoading(false);
    }
  }, [previsaoData, entregaData, filtroProduto, filtroMes, filtroAno]);

  // Carregar dados inicialmente
  useEffect(() => {
    fetchPrevisaoData();
    fetchEntregaData();
  }, []);

  return (
    <div className="container">
      <h1>Dados de Previsão e Entrega</h1>

      {loading && <p>Carregando...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div className="filters">
        <label>Produto:
          <select value={filtroProduto} onChange={(e) => setFiltroProduto(e.target.value)}>
            <option value="">Todos</option>
            {previsaoData.map((item, index) => (
              <option key={index} value={item['Produto']}>{item['Produto']}</option>
            ))}
          </select>
        </label>
        <label>Mês:
          <select value={filtroMes} onChange={(e) => setFiltroMes(e.target.value)}>
            <option value="">Todos</option>
            <option value="1">Janeiro</option>
            <option value="2">Fevereiro</option>
            <option value="3">Março</option>
            <option value="4">Abril</option>
            <option value="5">Maio</option>
            <option value="6">Junho</option>
            <option value="7">Julho</option>
            <option value="8">Agosto</option>
            <option value="9">Setembro</option>
            <option value="10">Outubro</option>
            <option value="11">Novembro</option>
            <option value="12">Dezembro</option>
          </select>
        </label>
        <label>Ano:
          <select value={filtroAno} onChange={(e) => setFiltroAno(e.target.value)}>
            <option value="">Todos</option>
            {previsaoData.map((item, index) => {
              const ano = new Date(item['Data de Previsão de Entrega']).getFullYear();
              return <option key={index} value={ano}>{ano}</option>;
            })}
          </select>
        </label>
      </div>

      <table>
        <thead>
          <tr>
            <th>Código</th>
            <th>Produto</th>
            <th>Quantidade Prevista</th>
            <th>Data de Previsão de Entrega</th>
            <th>Semana da Previsão</th>
            <th>Quantidade Entregue</th>
            <th>Quantidade Faltante</th>
            <th>Data de Entrega Real</th>
          </tr>
        </thead>
        <tbody>
          {combinedData.map((row, index) => (
            <tr key={index}>
              <td>{row['Codigo']}</td>
              <td>{row['Produto']}</td>
              <td>{row['Quantidade Prevista']}</td>
              <td>{row['Data de Previsão de Entrega']}</td>
              <td>{row['SemanaPrevisao']}</td>
              <td>{row['QuantidadeEntregue']}</td>
              <td style={{ backgroundColor: row['QuantidadeFaltante'] > 0 ? 'red' : row['QuantidadeFaltante'] === 0 ? 'green' : 'orange' }}>
                {row['QuantidadeFaltante']}
              </td>
              <td>{row['DataEntregaReal']}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
