import React, { useEffect, useState } from "react";
import { getSheetData } from "./googleSheets"; // Importa a função de requisição

const App = () => {
  const [data, setData] = useState([]); // Estado para armazenar os dados da planilha

  useEffect(() => {
    // Função assíncrona para pegar os dados da planilha
    const fetchData = async () => {
      const sheetData = await getSheetData(); // Chama a função de requisição
      setData(sheetData); // Armazena os dados no estado
    };

    fetchData(); // Chama a função de obtenção de dados
  }, []); // O array vazio significa que isso vai acontecer apenas uma vez, quando o componente for montado

  return (
    <div>
      <h1>Dados da Planilha do Google Sheets</h1>
      <table>
        <thead>
          <tr>
            <th>Nome do Produto</th>
            <th>Previsão</th>
            <th>Entrega</th>
            <th>Quantidade</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              {row.map((cell, cellIndex) => (
                <td key={cellIndex}>{cell}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
